import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import { doc, getDoc } from "firebase/firestore";
import { db } from "../firebase";
import "./ViewDetails.css"; // We’ll use or create this CSS file

function ViewDetails() {
  const { studentid } = useParams();
  const [studentData, setStudentData] = useState(null);

  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const docRef = doc(db, "students", studentid);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          setStudentData({ id: docSnap.id, ...docSnap.data() });
        } else {
          console.log("No such document!");
        }
      } catch (err) {
        console.log("Error fetching student:", err.code, err.message);
      }
    };
    fetchStudent();
  }, [studentid]);

  if (!studentData) {
    return (
      <div className="container">
        <h1>Loading...</h1>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>Student Details</h1>
      <div className="details">
        <p><strong>ID: </strong>{studentData.id}</p>
        <p><strong>NAME: </strong>{studentData.name}</p>
        <p><strong>PLACE: </strong>{studentData.place}</p>
        <p><strong>PHONE: </strong>{studentData.phone}</p>
        <div className="button-container"> {/* Matches EditStudent layout */}
          <Link to="/" className="btn-back">Back</Link>
        </div>
      </div>
    </div>
  );
}

export default ViewDetails;
