import { Link, useNavigate } from "react-router-dom";
import './StudentTable.css';
import { useEffect, useState } from "react";

// ðŸ‘‰ NEW: Import Firestore tools
import { db } from "../firebase"; // âœ… Ensure firebase.js exists
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";

function StudentTable() {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  const DisplayDetails = (id) => {
    navigate("/student/view/" + id);
  };

  const EditDetails = (id) => {
    navigate("/student/edit/" + id);
  };

  const RemoveDetails = async (id) => {
    try {
      await deleteDoc(doc(db, "students", id));
      alert("Removed Student Data Successfully");
      fetchStudents(); // Refresh list
    } catch (err) {
      console.log(err.message);
    }
  };

  const fetchStudents = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "students"));
      const studentList = querySnapshot.docs.map((doc) => ({
        id: doc.id, // Firestore document ID
        ...doc.data(), // Student fields
      }));
      setStudents(studentList);
    } catch (err) {
      console.log("Error fetching students:", err.message);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div className="container">
      <h2>Student Records</h2>
      <div className="table-conatiner">
        <Link to="/student/create" className="btn btn-add">Add New Student</Link>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Place</th>
              <th>Phone</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.place}</td>
                <td>{item.phone}</td>
                <td>
                  <button onClick={() => DisplayDetails(item.id)} className="btn btn-info">View</button>
                  <button onClick={() => EditDetails(item.id)} className="btn btn-primary">Edit</button>
                  <button onClick={() => RemoveDetails(item.id)} className="btn btn-danger">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
      );
    }
    
    export default StudentTable;
