// src/StudentTable.js
import { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom"
import { doc, getDoc } from "firebase/firestore";
import { db } from "../firebase";
import "./ViewDetails.css";


function ViewDetails() {
  const {studentid}=useParams();
  const [studentData,setStudentData]=useState({});
  useEffect(() => {
    const fetchStudent = async () => {
      try {
        const docRef = doc(db, "students", studentid);
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          // Combine the ID and data into one object
          setStudentData({ id: docSnap.id, ...docSnap.data() });
        } else {
          console.log("No such document!");
        }
      } catch (err) {
        console.log("Error fetching student:", err.code, err.message);
      }
    };
    fetchStudent();
  }, [studentid]);
  
  return (
    <div className="container">
      <h1>Student Details</h1>
     { studentData &&< div className="details">
      <p><strong>ID: </strong>{studentData.id || "N/A"}</p>
      <p><strong>NAME: </strong>{studentData.name || "N/A"}</p>
      <p><strong>PLACE: </strong>{studentData.place || "N/A"}</p>
      <p><strong>PHONE: </strong>{studentData.phone || "N/A"}</p>
      </div>
      }
      <Link to="/" className="btn btn-back">back</Link>
    </div> 
  )
  }
  export default ViewDetails;
