import { Link, useNavigate } from "react-router-dom";
import "./StudentTable.css";
import { useEffect, useState } from "react";
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";
import { db } from "../firebase";

function StudentTable() {
  const [students, setStudents] = useState([]);
  const navigate = useNavigate();

  const DisplayDetails = (id) => {
    navigate("/student/view/" + id);
  };

  const EditDetails = (id) => {
    navigate("/student/edit/" + id);
  };

  const RemoveDetails = async (docId) => {
    if (window.confirm("Are you sure you want to delete this student?")) {
      try {
        const docRef = doc(db, "students", docId);
        await deleteDoc(docRef);
        alert("Removed Student Data Successfully");
        fetchStudents();
      } catch (err) {
        console.log("Error deleting student:", err.message);
      }
    }
  };

  const fetchStudents = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "students"));
      const studentList = querySnapshot.docs.map((doc) => ({
        docId: doc.id,
        ...doc.data(),
      }));
      setStudents(studentList);
    } catch (err) {
      console.log("Error fetching students:", err.message);
    }
  };

  useEffect(() => {
    fetchStudents();
  }, []);

  return (
    <div className="container">
      <h2>Student Records</h2>
      <div className="table-container">
        <Link to="/student/create" className="btn-add">Add New Student</Link>
        <table>
          <thead>
            <tr>
              <th>ID</th><th>Name</th><th>Place</th><th>Phone</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {students.map((item) => (
              <tr key={item.docId}>
                {[
                  <td key="id">{item.id}</td>,
                  <td key="name">{item.name}</td>,
                  <td key="place">{item.place}</td>,
                  <td key="phone">{item.phone}</td>,
                  <td key="actions">
                    <button onClick={() => DisplayDetails(item.id)} className="btn-info">View</button>
                    <button onClick={() => EditDetails(item.id)} className="btn-primary">Edit</button>
                    <button onClick={() => RemoveDetails(item.docId)} className="btn-danger">Delete</button>
                  </td>
                ]}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default StudentTable;
